def download(mask_type=MASKS['MAIN_CREW'], output_folder=OUTPUT_FOLDER['MASK_MAIN_CREW']):
    path = os.path.join(ABSO_PATH, output_folder)
    if (not os.path.exists(path)):
        os.makedirs(path)

    for i in range(len(mask_type["full_names"])):
        url_name = mask_type["url_names"][i]
        full_name = mask_type["full_names"][i]

        url = f"https://fbi.paydaythegame.com/img/masks/full/{url_name}_dif.png"
        r = requests.get(url, stream=True)

        if r.status_code == 200:
            with open(f"./{output_folder}/{full_name}.png", "wb") as f:
                shutil.copyfileobj(r.raw, f)
            print(f"Downloaded {full_name} mask.")
        else:
            print(f"Error while downloading {full_name} mask")

download(MASKS['ALL_MASKS'], OUTPUT_FOLDER['MASK_ALL_MASKS'])